    public static String CIPHER_RC4 = "rc4";

    public static Map<String, String> getCiphers() {
        Map<String, String> ciphers = new HashMap<String, String>();
        ciphers.put(CIPHER_RC4, RC4_Cipher.class.getName());
        return ciphers;
    }

    public RC4_Cipher(byte[] key) {
        super(key);
    }

    //    @Override
    public int getKeyLength() {
        return RC4_Cipher.KEY_LENGTH;
    }

    public int getIVLength () {
        return RC4_Cipher.IV_LENGTH;
    }


    public RC4_Cipher(byte[] key, boolean ota, boolean setup_key) {
        super(key, ota, setup_key);
    }

    public RC4_Cipher(byte[] key, boolean ota) {
        super(key, ota, true);
    }

    public Generator<Byte> core() {
        Generator<Byte> n = new Generator<Byte>() {
            @Override
            protected void run() throws Exception {
                int[] data = IntStream.range(0, 256).toArray();
                int y = 0;
                for (int x : IntStream.range(0, 256).toArray()) {
                    y = key[x % getKeyLength()] + data[x] + y & 0xff;
                    int tmp = data[x];
                    data[x] = data[y];
                    data[y] = tmp;
                }
                int x = 0;
                y = 0;
                while (true) {
                    x = x + 1 & 0xff;
                    y = y + data[x] & 0xff;
                    int tmp = data[x];
                    data[x] = data[y];
                    data[y] = tmp;
                    yield((byte)data[data[x] + data[y] & 0xff]);
                }
            }
        };
        return n;
    }

    public void setup(){
//        System.out.println("StreamCipher... setup");
        super.setup();
    }

